%*************基里巴斯***********************
syms ri Ni(t) Nimax	s%符号定义
dNi = diff(Ni)	%EDPs的变化率
Ni = dsolve(dNi == ri*Ni(t)*(1 - Ni(t)/Nimax), Ni(2039)== s)
Ni = simplify(Ni)	%简化结果
%指导撤离曲线
M = diff(Ni)
M = simplify(M)

s = 2178  %初值
Nimax = 243118  %最大值
ri = 0.13            %EDPs的变化率
t = 2020:0.1:2200	%时间-年份
Ni = (Nimax.*s.*exp(ri.*(t - 2039)))./(Nimax - s + s.*exp(ri.*(t - 2039)))
M = (Nimax.*ri.*s.*exp(ri.*(t - 2039)).*(Nimax - s))./(Nimax - s + s.*exp(ri.*(t - 2039))).^2
%画出重量随时间变化的模型曲线

plot(t,M)
hold on;
all=all+M;

